import { isDate } from 'lodash';
import { format } from 'date-fns'
export function useSetTab(statusList:any, ext:any, count?: number){
    // 顶部 状态tab签 数据计算回显
    if(!ext) return
    let total = 0
    let hasAllCount = false
    for (let item in ext) {
        if(item === 'ALL') hasAllCount = true
        total += ext[item]
        if(statusList?.value[item]) {
            statusList.value[item].count = ext[item] || 0
        }
    }
    if(!hasAllCount) statusList.value['ALL'].count = total || count

}

// 获取完整地址
export function useFullAddress(addressObj:any){
    if(!addressObj) return '-'
    let {province, city, county, address} = addressObj
    return (province || '') + ' ' + (city || '') + ' ' + (county || '') + ' ' + (address || '')
}
// 取 省市区 地址
export function usePartAddress(addressObj:any){
    if(!addressObj) return '-'
    let {province, city, county} = addressObj
    return (province || '') + ' ' + (city || '') + ' ' + (county || '')
}
// 取联系人 \  电话
export function useContact(contact: any){
    if(!contact) return '-'
    let {name, phone: {number}} = contact
    return `${name || '-'} / ${number || '-'}`
}

// 取联系方式
export function usePhone(contact: any){
    if(!contact) return '-'
    let {phone: {number}} = contact
    return `${number || '-'}`
}
export function usePurePhone(phone:string) {
    if (!phone) return '';
    // 仅匹配开头的 +86、86，且后面紧跟数字或分隔符
    const regex = /^(\+86|86)([\s-]?)/;
    return phone.replace(regex, '').trim();
}
// 图片加载失败，容错图
export function useErrorImg(){
    return "data:image/png;base64,"
}

// 格式化日期
export function useDate(date:any, pattern = 'yyyy-MM-dd HH:mm:ss', hideError?: boolean) {
    if (!isDate(date)) {
        date = new Date(date);
    }
    if (isNaN(date.getTime())) {
        return hideError ? null  : '无效日期';
    }
    return format(date, pattern);
}

export function useList(list: string[]){
    let html = list.map((item) => `<li>${item}</li>`)
    return `<ul class="indent-reset">${html.join('')}</ul>`;
}


export function useIndex(pagination:any, index:number){
    let pageSize = pagination?.pageSize as number;
    let pageIndex = pagination?.current as number;
    let Index
    if(!isNaN(pageSize)) {
        Index = pageSize >1 ? pageSize * (pageIndex - 1) + index + 1 : index + 1;
    } else {
        Index = index + 1;
    }
    return Index;
}
